$paths=(Get-ChildItem -Path $env:windir -File -Recurse -ErrorAction SilentlyContinue | Where-Object { $_.FullName.Length -lt 140 } | Get-Random -Count 30 | Select-Object -ExpandProperty FullName); $total=$paths.Count*12; $step=0; foreach($p in $paths){ for($i=0;$i -lt 12;$i++){ $step++; $pct=[int](($step/$total)*100); Write-Progress -Activity "Disk cleanup" -Status "Removing: $p" -PercentComplete $pct; Start-Sleep -Milliseconds (Get-Random -Minimum 40 -Maximum 60) } Write-Host "Removing: $p" -ForegroundColor Yellow; Start-Sleep -Milliseconds 100; Write-Host "Removed:  $p" -ForegroundColor DarkGreen }; Write-Progress -Activity "Disk cleanup" -Completed; Write-Host "`nEating all your files - nom nom nom" -ForegroundColor Cyan
